INSERT INTO public.tours (title, description, location, duration, price, image_url, highlights, inclusions, exclusions, itinerary) VALUES
('3 Days 2 Nights Nanyuki Adventure', 'An adventure-packed retreat combining adrenaline activities with nature exploration. Located at the Equator, Nanyuki offers unique experiences from white-water rafting to forest canopy walks and pristine natural pools.', 'Nanyuki, Kenya', '3 Days / 2 Nights', 750, '/images/experiences/3-days-nanyuki-adventure/featured.jpg', '["Sagana River white-water rafting (1.5-2 hours)", "Ngare Ndare forest canopy walk experience", "Waterfall hiking and swimming in azure natural pools", "Natural pool swimming in crystal-clear waters", "OlPejeta Conservancy wildlife viewing", "Equator crossing experience and photos", "Full board accommodation with vegetarian options", "Professional water facilitators and guides"]', '["Professional guide and driver", "All meals (breakfast, lunch, dinner)", "Accommodation at selected lodge", "Park entrance fees", "White-water rafting equipment", "Canopy walk experience", "Transportation in safari vehicle"]', '["International flights", "Travel insurance", "Personal expenses", "Tips and gratuities", "Optional activities not mentioned"]', 'Day 1: Nairobi - Nanyuki - Water Rafting. Drive to Nanyuki in clean safari vehicle. Afternoon white-water rafting adventure on Sagana River, 1.5-2 hours guided by professional water facilitators. Cross the Equator for unique experience and photos. Check-in at lodge for dinner and overnight.\n\nDay 2: Ngare Ndare Full-Day Adventure. Full day at Ngare Ndare Forest. Experience canopy walk through the treetops. Exciting and thrilling hike to waterfalls. Swimming in azure natural pools surrounded by pristine forest.\n\nDay 3: OlPejeta Conservancy - Departure. Morning visit to OlPejeta Conservancy for wildlife viewing and conservation experience. Return to Nairobi in clean safari vehicle.'),
('5 Days Wasini Experience', 'Explore the pristine waters and marine life of Wasini Island. This experience combines snorkeling, dolphin watching, and island exploration with authentic Swahili culture and cuisine.', 'Wasini Island, Kenya', '5 Days / 4 Nights', 1200, '/images/experiences/5-days-wasini-experience/featured.jpg', '["Dolphin watching and marine wildlife", "Snorkeling in pristine coral reefs", "Kisite-Mpunguti Marine National Park", "Authentic Swahili cuisine", "Island exploration", "Beach relaxation"]', '["Accommodation on Wasini Island", "All meals with Swahili specialties", "Snorkeling equipment", "Dolphin watching boat tour", "Island guide services", "Beach activities"]', '["International flights", "Travel insurance", "Personal expenses", "Tips and gratuities"]', 'Day 1: Arrival and orientation at Wasini Island. Day 2: Snorkeling and dolphin watching in Kisite-Mpunguti Marine Park. Day 3: Island exploration and Swahili cultural experience. Day 4: Beach relaxation and water activities. Day 5: Departure.'),
('3 Days in Mombasa', 'Experience the vibrant coastal city of Mombasa with its rich Swahili heritage, pristine beaches, and historic landmarks including Fort Jesus and the old town.', 'Mombasa, Kenya', '3 Days / 2 Nights', 650, '/images/experiences/3-days-mombasa/featured.jpg', '["Mombasa old town exploration", "Fort Jesus historic site", "Swahili cultural heritage", "Pristine beaches", "Local cuisine", "Water sports activities"]', '["Accommodation in selected hotel", "Daily breakfast", "Local guide services", "Transportation", "Fort Jesus entrance fee"]', '["International flights", "Travel insurance", "Meals not specified", "Tips"]', 'Day 1: Arrival and old town tour. Day 2: Fort Jesus and beach exploration. Day 3: Water sports and departure.'),
('5 Days Watamu Beach Experience', 'Relax and rejuvenate at the stunning Watamu Beach with pristine white sands, turquoise waters, and coral reef snorkeling opportunities.', 'Watamu, Kenya', '5 Days / 4 Nights', 950, '/images/experiences/5-days-watamu-beach/featured.jpg', '["Watamu Beach relaxation", "Coral reef snorkeling", "Marine life viewing", "Water sports", "Beach bars and dining", "Sunset viewing"]', '["Beachfront accommodation", "All meals", "Snorkeling equipment", "Beach activities", "Local guide"]', '["International flights", "Travel insurance", "Personal expenses", "Alcohol not included"]', 'Day 1: Check-in and beach orientation. Day 2-4: Beach activities and water sports. Day 5: Departure.'),
('10 Days Kenya-Tanzania Safari Itinerary', 'An epic safari adventure combining the best of Kenya and Tanzania, featuring the Serengeti, Masai Mara, and pristine wildlife viewing.', 'Kenya & Tanzania', '10 Days / 9 Nights', 2500, '/images/experiences/10-days-kenya-tanzania/featured.jpg', '["Serengeti National Park", "Masai Mara Safari", "Big Five viewing", "Professional guides", "Luxury accommodation", "Wildlife photography"]', '["All accommodation", "Meals included", "Park fees", "Professional guides", "Transportation"]', '["International flights", "Travel insurance", "Personal expenses", "Gratuities"]', 'Day 1-3: Kenya exploration. Day 4-7: Serengeti adventure. Day 8-10: Final safari and departure.'),
('5 Days Masai Mara Safari', 'Experience the iconic Masai Mara National Reserve with its incredible wildlife, rolling plains, and authentic Maasai culture encounters.', 'Masai Mara, Kenya', '5 Days / 4 Nights', 1350, '/images/experiences/5-days-masai-mara/featured.jpg', '["Masai Mara game drives", "Big Five viewing", "Hot air balloon safari", "Maasai cultural visits", "Sunset game drives", "Professional naturalist guides"]', '["Luxury lodge accommodation", "All meals", "Game drives", "Park entrance fees", "Professional guides"]', '["International flights", "Travel insurance", "Alcoholic beverages", "Personal expenses"]', 'Day 1: Arrival and settlement. Day 2-4: Daily game drives. Day 5: Departure.'),
('6 Days Nairobi City Tour and Mara Adventure', 'Combine the urban excitement of Nairobi with the wild adventures of Masai Mara in this comprehensive Kenyan experience.', 'Nairobi & Masai Mara, Kenya', '6 Days / 5 Nights', 1600, '/images/experiences/6-days-nairobi-mara/featured.jpg', '["Nairobi city tour", "Giraffe Centre", "Karen Blixen Museum", "Masai Mara safari", "Hot air balloon ride", "Cultural encounters"]', '["Nairobi accommodation", "Mara lodge stay", "All meals", "City tours", "Game drives"]', '["International flights", "Travel insurance", "Personal items", "Tips"]', 'Day 1: Nairobi city orientation. Day 2: Giraffe Centre and museums. Day 3-5: Masai Mara adventure. Day 6: Return to Nairobi and departure.'),
('Lake Nakuru National Park Itinerary', 'Discover the alkaline Lake Nakuru, famous for its flamingos, rhinos, and diverse bird species in this scenic national park.', 'Lake Nakuru, Kenya', '2 Days / 1 Night', 450, '/images/experiences/lake-nakuru/featured.jpg', '["Lake Nakuru flamingos", "Rhino sanctuary", "Bird watching", "Scenic drives", "Nature photography", "Professional guides"]', '["Park lodge accommodation", "All meals", "Game drives", "Park fees", "Guide services"]', '["International flights", "Travel insurance", "Personal expenses"]', 'Day 1: Arrival and game drive. Day 2: Flamingo and wildlife viewing, departure.'),
('14 Days Kenya Safari Itinerary', 'The ultimate Kenya safari experience with extended time in multiple parks including Amboseli, Tsavo, and Masai Mara.', 'Kenya', '14 Days / 13 Nights', 3500, '/images/experiences/14-days-kenya-safari/featured.jpg', '["Extended park visits", "Diverse wildlife viewing", "Multiple accommodations", "Professional guides", "Photography opportunities", "Cultural tours"]', '["All accommodation", "All meals", "Park fees", "Transportation", "Professional guides"]', '["International flights", "Travel insurance", "Gratuities", "Alcohol"]', 'Days 1-3: Amboseli. Days 4-7: Tsavo. Days 8-11: Masai Mara. Days 12-14: Final experiences and departure.'),
('8 Days Kenya Safari Itinerary', 'An extended Kenyan safari hitting the highlights of the country''s best parks and reserves with expert guides.', 'Kenya', '8 Days / 7 Nights', 2200, '/images/experiences/8-days-kenya-safari/featured.jpg', '["Multiple park visits", "Wildlife viewing", "Expert guides", "Quality accommodation", "Diverse landscapes", "Photography tours"]', '["All lodges", "Meals included", "Park entrance fees", "Transportation", "Guide services"]', '["International flights", "Travel insurance", "Personal items", "Alcoholic drinks"]', 'Day 1-2: First park. Day 3-5: Main safari. Day 6-7: Final park. Day 8: Departure.'),
('4 Days Adventure Safari', 'Combine adventure activities with traditional safari in this action-packed tour covering multiple game reserves.', 'Olpejeta, Nakuru & Amboseli, Kenya', '4 Days / 3 Nights', 1000, '/images/experiences/4-days-adventure-safari/featured.jpg', '["OlPejeta Conservancy", "Lake Nakuru", "Amboseli Park", "Wildlife viewing", "Adventure activities", "Expert guides"]', '["Adventure lodge accommodation", "All meals", "Park fees", "Transportation", "Equipment rental"]', '["International flights", "Travel insurance", "Personal expenses", "Tips"]', 'Day 1: OlPejeta visit. Day 2: Lake Nakuru exploration. Day 3: Amboseli adventure. Day 4: Return and departure.'),
('2 Nights 3 Days Amboseli Sopa Lodge Safari', 'Experience the majestic Mount Kilimanjaro and abundant wildlife at Amboseli National Park from the luxury Sopa Lodge.', 'Amboseli, Kenya', '3 Days / 2 Nights', 1100, '/images/experiences/2-nights-3-days-amboseli-safari/featured.jpg', '["Mount Kilimanjaro views", "Wildlife photography", "Elephant herds", "Expert naturalist guides", "Luxury accommodation", "Traditional Maasai visits"]', '["Sopa Lodge accommodation", "All meals", "Daily game drives", "Park entrance", "Professional guide"]', '["International flights", "Travel insurance", "Tips and gratuities", "Personal expenses"]', 'Day 1: Arrival and afternoon game drive. Day 2: Full day safari with Kilimanjaro backdrop. Day 3: Morning drive and departure.'),
('4 Days Mombasa Package', 'An all-inclusive beach and city package combining Mombasa''s cultural attractions with pristine beaches and island activities.', 'Mombasa, Kenya', '4 Days / 3 Nights', 900, '/images/experiences/4-days-mombasa/featured.jpg', '["Mombasa city tour", "Island boat trips", "Beach relaxation", "Local cuisine", "Water sports", "Historic sites"]', '["Hotel accommodation", "All meals", "City tour guide", "Boat tours", "Transportation"]', '["International flights", "Travel insurance", "Personal items", "Alcohol extra"]', 'Day 1: City arrival and orientation. Day 2: Island boat trip. Day 3: Beach and water sports. Day 4: Departure.'),
('3 Days 2 Nights Masaai Mara Safari', 'Immerse yourself in the Masai Mara with expert guides for authentic wildlife encounters and Maasai cultural experiences.', 'Masai Mara, Kenya', '3 Days / 2 Nights', 850, '/images/experiences/3-days-masai-mara/featured.jpg', '["Game drives", "Maasai village visit", "Cultural experience", "Big Five viewing", "Photography", "Expert guides"]', '["Mara lodge", "All meals", "Game drives", "Cultural tour", "Professional guide"]', '["International flights", "Travel insurance", "Tips", "Personal expenses"]', 'Day 1: Arrival and first game drive. Day 2: Full day safari and Maasai visit. Day 3: Morning drive and departure.');
